import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from 'react-redux';
import {notesById} from '../../../redux/actions/notes'
import BarNote from "../../ui/BarNote";
import CardNote from "../../ui/note-card";
import { USER_ID } from '../../../utils/local-Storage.utils';
const Notes = () => {
  const [click, setClick] = useState(false);


  const dispatch = useDispatch();
  const { loading, notes } = useSelector(state => state.listNotes);

  useEffect( () => {
    console.log(USER_ID, 'USER_ID of notes')
      dispatch(notesById(sessionStorage.getItem('user_id')));
  },[dispatch]);

  // const [state, setState] = useState({
  //   title: "",
  //   note: "",
  // });

  // const handleChange = (evt) => {
  //   evt.preventDefault();
  //   const value = evt.target.value;
  //   setState({
  //     ...state,
  //     [evt.target.name]: value,
  //   });
  // };

  const submit = (e) => {
    e.preventDefault();

    setClick(false);
  };
  // const Blur=()=>{
  //   setClick(false)
  // }

  const onClick = (e) => {
    e.preventDefault();
    setClick(true);
  };

  return (
    <div className="right-bar">
      <div className="noted ">
        <BarNote
          onclick={(e) => {
            onClick(e);
          }}
          submitB={(e) => submit(e)}
          click={click}
        />

        <br />
      </div>

      <div className="dp-f display-notes  ">





  {!loading ? (
    notes && notes.length > 0?
      (
        <div className="sub-display ">

    {
      notes.map(({id,title,note})=>(

        <CardNote key={id} title={title} note={note} /> 

      ))
    }

        </div>
      ):(<div>No Notes</div>)

   ):(
     <div>loading Notes</div>
   )}

      </div>
    </div>
  );
};
export default Notes;
