/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useRef, useState } from "react";
import Icons from "../assets/Icons";

const Dashboard = () => {
  const initialState = { backgroundColor: "#f1f3f4" };
  const textInput = useRef("");
  const [active, setactive] = useState(false)
  const final = { backgroundColor: "#ffffff", boxShadow: "  inset 1px 1px 0 rgba(0,0,0,.1), inset 0 -1px 0 rgba(0,0,0,.07)" };
  const [state, setstate] = useState(initialState);

  const click = () => {
    setactive(true)
    textInput.current.focus();
  };
  const out = () => {
    setactive(false)
    textInput.current.focus();
  };
  return (
    <div className="nav-bar dp-f border-2px">
      <div className="keep-icon dp-f ">
        <div className="list-3 icon">
          <Icons focusable="false" name="List" size="24" />
        </div>
        <div className="name-page">
          <a href="" className="dp-f">
            <img
              className="img "
              src="https://www.gstatic.com/images/branding/product/1x/keep_2020q4_48dp.png"
              srcset="https://www.gstatic.com/images/branding/product/1x/keep_2020q4_48dp.png 1x, https://www.gstatic.com/images/branding/product/2x/keep_2020q4_48dp.png 2x "
              alt=""
              aria-hidden="true"
              style={{ width: "40px", height: "40px" }}
            ></img>
            <span className="span ">
              <h6>Keep</h6>
            </span>
          </a>
        </div>
      </div>
       <div className={active? "active search-div": "search-div border-2px"} >
       <form action="" method="get" classNam="dp-f">
         <button className="icon search-icon ">
         <Icons focusable="false" name="Search" size="24" />
         </button>
         <div className="research ">
           <input type="search" onFocus={click} onfocusout={out}/>

         </div>

         <button className=" cancel-icon icon" onClick={()=>setactive(false)}>
         <Icons focusable="false" name="Cross" size="24" />
         </button>
       </form>
       </div>
      <div className="icons-end border-2px">
        <div className="refresh-mode"></div>
        <div className="user-show"></div>
      </div>
    </div>
  );
};
export default Dashboard;
