import React, { useState } from "react";
import GoggleKepp from "../assets/images/logo-google-keep.png";
import LandingSignup from "../assets/images/undraw_completing_6bhr.svg";
import { Link } from "react-router-dom";
import Button from "../components/layout/button";
import LabelInput from '../components/ui/label-input'

export default function Signup() {

    const [visibility,setVisisbility] = useState(false);


   const handleInput = ({ target: { name, value } }) => {
      const { visibility } = this.state;
      if (!visibility) {
          this.setState({
              visibility: true
          });
      }
      if (name !== 'description' && value !== '') {
          if (
              Number.isNaN(parseInt(value.split('')[value.split('').length - 1], 0))
          ) {
              //   toastError('Please Input Integer Value', "Input Error", 5);
              return;
          }
      }
      this.setState({
          [name]: value
      });
  };

  return (
    <div id="Signup" className="row">
      <div
        className="column border-2px"
        style={{ width: "50%", height: "100%" }}
      >
        <div className="">
          <div className="logo-description border-2px">
            <img src={GoggleKepp} alt="" srcset="" />
            <h1>Create Your Account Here! </h1>
          </div>
          <form action="" className=" border-2px">
            <div className=" dp-flex names">

                <LabelInput
             className="names fs-14 br-12"
                  type="text"
                  placeholder={"First Name"}
                  visible={visible.toString()}
                />


              <label htmlFor="First Name">
                <input
                  className="names fs-14 br-12"
                  type="text"
                  placeholder="Last Name"
                />
              </label>
            </div>
            <br />
            <br />
            <input
              className="email-passwor fs-14 br-12"
              type="email"
              placeholder="Email"
            />

            <br />
            <br />

            <input
              className="email-password fs-14 br-12"
              type="password"
              placeholder="Password"
            />
            <br />
            <br />
            <div className="submit0button">
              <Button />
            </div>
            <br />
            <br />
            <div className="already-have-account">
              <div className="sub-to-Signup">
                <Link id="fw-light">
                  Already Have an Account? Signup Here!!
                </Link>
              </div>
            </div>
          </form>
        </div>
      </div>
      <div
        className="column border-2px"
        style={{ width: "50%", height: "100%" }}
      >
        <div className="side-landing-img">
          <h2>
            Let's Help You <span>Keep ,Create ,Share and Collaborate </span>your
            note list
          </h2>
          <img src={LandingSignup} alt="" />
        </div>
      </div>
    </div>
  );
}
