/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useRef, useState } from "react";
import Icons from "../assets/Icons";

const Dashboard = () => {
  const initialState = { backgroundColor: "#f1f3f4" };
  const [active, setactive] = useState(false);
  const [cancel, setcancel] = useState(false);
  const [state] = useState(initialState);

  const click = () => {
    setactive(true);
    setcancel(true);
  };
  const out = () => {
    setactive(false);
  };
  const handleSearch = (evt) => {
    evt.preventDefault();
    setactive(false);
    setcancel(true);
  };

  return (
    <div className="nav-bar dp-f border-2px">
      <div className="keep-icon dp-f ">
        <div className="list-3 icon">
          <Icons focusable="false" name="List" size="24" />
        </div>
        <div className="name-page">
          <a href="" className="dp-f">
            <img
              className="img "
              src="https://www.gstatic.com/images/branding/product/1x/keep_2020q4_48dp.png"
              srcset="https://www.gstatic.com/images/branding/product/1x/keep_2020q4_48dp.png 1x, https://www.gstatic.com/images/branding/product/2x/keep_2020q4_48dp.png 2x "
              alt=""
              aria-hidden="true"
              style={{ width: "40px", height: "40px" }}
            ></img>
            <span className="span ">
              <h6>Keep</h6>
            </span>
          </a>
        </div>
      </div>
      <div
        className={active ? "active search-div" : "search-div "}
        style={state}
        onFocus={click}
        onBlur={out}
      >
        <form
          action=""
          method="get"
          classNam="dp-f"
          onSubmit={() => alert("submitted")}
        >
          <button className="icon search-icon ">
            <Icons focusable="false" name="Search" size="24" />
          </button>
          <div className="research ">
            <input type="text" placeholder="Search" />
          </div>

          {cancel ? (
            <button
              className=" cancel-icon icon"
              onClick={(e) => handleSearch(e)}
            >
              <Icons focusable="false" name="Cross" size="24" />
            </button>
          ) : (
            ""
          )}
        </form>
      </div>
      <div className="dp-f icons-end border-2px">
        <div className="dp-f refresh-mode border-2px">
          <div className="side-icons" >
            <Icons focusable="false" name="Refresh" viewBox="-5 -3 30 30"   className="icons-n"/>
          </div>
          <div className="side-icons">
            <Icons focusable="false" name="GridMode" size="30" />
          </div>
          <div className="   side-icons">
            {" "}
            <Icons focusable="false" name="Settings" size="30" />
          </div>
        </div>
        <div className="  dp-f user-show border-2px">
          <div className="   side-icons">
            <Icons focusable="false" name="Mult" size="30" />
          </div>
          <div className=" circle  side-icons">
            <Icons focusable="false" name="UserCircle" size="40" />
          </div>
        </div>
      </div>
    </div>
  );
};
export default Dashboard;
