import React, { useState } from "react";
import GoggleKepp from "../assets/images/logo-google-keep.png";
import LandingSignup from "../assets/images/undraw_completing_6bhr.svg";
import { Link } from "react-router-dom";
import LabelInput from "../components/ui/label-input";

export default function Signup() {
  const [visibility, setVisisbility] = useState(false);
  const [state, setState] = useState({
    first_name: "",
    last_name: "",
    email: "",
    password: "",
  });

const noNumbers=(value)=>{

  if (/[^a-zA-Z]/.test(value)) {
    console.log(JSON.stringify(value), "=> invalid");
} else {
    console.log(JSON.stringify(value), "=> valid");
}
}

  const handleInput = (evt) => {
    const value = evt.target.value;




  };
   const KeyPress=(event)=> {
    return (event.charCode >= 65 && event.charCode <= 90) || (event.charCode >= 97 && event.charCode <= 122);
  }

  return (
    <div id="Signup" className="row">
      <div
        className="column "
        style={{ width: "50%", height: "100%" }}
      >
        <div className="">
          <div className="logo-description ">
            <img src={GoggleKepp} alt="" srcset="" />
            <h1>Create Your Account Here! </h1>
          </div>

          <br/>
          <form action="" className="signup-form add-diagnose add-vitals " >
            <div className="row dp-f">
              <LabelInput
              onKeyPress={event => KeyPress(event)}
                visible={visibility.toString()}
                onChange={handleInput}
                type="text"
                className="input-doc "
                placeholder="First Name"
                name="first_name"
                pattern = "[A-Za-z]"
                value={state.first_name}
              />

              <LabelInput
                visible={visibility.toString()}
                onChange={handleInput}
                type="text"
                className="input-doc "
                placeholder="Last Name"
                name="last_name"
                value={state.last_name}
              />
            </div>

            <div className="row">
              <LabelInput
                visible={visibility.toString()}
                onChange={handleInput}
                type="text"
                className="input-doc "
                placeholder="Email"
                name="email"
                value={state.email}
              />
            </div>

            <div className="row">
              <LabelInput
                visible={visibility.toString()}
                onChange={handleInput}

                className="input-doc "
                placeholder="Password"
                name="password"
                type="password"
                value={state.password}
              />
            </div>

            <div className="row">
            <div className="col" >
            <button id="button" className="br-12"> Sign Up</button>
        </div>
            </div>

            <div className="already-have-account">
              <div className="sub-to-Signup">
                <Link id="fw-light">
                  Already Have an Account? Signup Here!!
                </Link>
              </div>
            </div>
          </form>
        </div>
      </div>
      <div
        className="column "
        style={{ width: "50%", height: "100%" }}
      >
        <div className="side-landing-img">
          <h2>
            Let's Help You <span>Keep ,Create ,Share and Collaborate </span>your
            note list
          </h2>
          <img src={LandingSignup} alt="" />
        </div>
      </div>
    </div>
  );
}
