import React, { useState } from "react";
import GoggleKepp from "../assets/images/logo-google-keep.png";
import LandingSignup from "../assets/images/undraw_completing_6bhr.svg";
import { Link } from "react-router-dom";
import Button from "../components/layout/button";
import LabelInput from '../components/ui/label-input'

export default function Signup() {

    const [visibility,setVisisbility] = useState(false);
    const [state, setState] = useState({
      first_name: '',
      last_name: '',
      email: '',
      password: ''
  });

  const handleInput = evt => {
    const value = evt.target.value;
    setState({
        ...state,
        [evt.target.name]: value
    });
    if (!visibility) {
      setVisisbility(!visibility);
    }

    console.log("States: ",state);
};

  return (
    <div id="Signup" className="row">
      <div
        className="column border-2px"
        style={{ width: "50%", height: "100%" }}
      >
        <div className="">
          <div className="logo-description border-2px">
            <img src={GoggleKepp} alt="" srcset="" />
            <h1>Create Your Account Here! </h1>
          </div>
          <form action="" className="add-diagnose add-vitals border-2px">
            <div className="row    border-2px">

            <LabelInput
            visible={visibility.toString()}
            onChange={handleInput}
            type="text"
            className="input-doc "
            placeholder="First Name"
            name="first_name"
            value={state.first_name}
        />


        <LabelInput
        visible={visibility.toString()}
        onChange={handleInput}
        type="text"
        className="input-doc "
        placeholder="Last Name"
        name="last_name"
        value={state.last_name}
    />
            </div>
            <br />
            <br />
            <input
              className="email-passwor fs-14 br-12"
              type="email"
              placeholder="Email"
            />

            <br />
            <br />

            <input
              className="email-password fs-14 br-12"
              type="password"
              placeholder="Password"
            />
            <br />
            <br />
            <div className="submit0button">
              <Button />
            </div>
            <br />
            <br />
            <div className="already-have-account">
              <div className="sub-to-Signup">
                <Link id="fw-light">
                  Already Have an Account? Signup Here!!
                </Link>
              </div>
            </div>
          </form>
        </div>
      </div>
      <div
        className="column border-2px"
        style={{ width: "50%", height: "100%" }}
      >
        <div className="side-landing-img">
          <h2>
            Let's Help You <span>Keep ,Create ,Share and Collaborate </span>your
            note list
          </h2>
          <img src={LandingSignup} alt="" />
        </div>
      </div>
    </div>
  );
}
