import {getAllUsers} from '../api/user.api'

 class User {

    constructor() {
        this.respond = {
            success: false,
            response: null,
            message: 'An error occured while connecting to the server!'
        };
    }

  setResponse = data => {
    this.respond = data;
};

getResponse = () => {
    return this.respond;
};

addUser = async user => {
    await createUser(user).then(({ data }) => {
        const { success, message, response } = data;
        if (success) {
            this.setResponse(response);
            toastSuccess(message);
        } else {
            toastError(message);
            this.setResponse([]);
        }
    });
};

getUsers = async () => {

    await getAllUsers().then(( result ) => {
        const { data } = result;
        const {success,response,message}=data;

        if (success) {
            this.setResponse(response);
        } else {
            console.log(message);
        }
    });
};
}
export default new User();
