import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from 'react-redux';
import {listUser} from '../../../redux/actions/user'
import BarNote from "../../ui/BarNote";
import CardNote from "../../ui/note-card";
const Notes = () => {
  const [click, setClick] = useState(false);


  const dispatch = useDispatch();
  const { loading, users } = useSelector(state => state.listUsers);

  useEffect(() => {
      dispatch(listUser());
  },[dispatch]);

  // const [state, setState] = useState({
  //   title: "",
  //   note: "",
  // });

  // const handleChange = (evt) => {
  //   evt.preventDefault();
  //   const value = evt.target.value;
  //   setState({
  //     ...state,
  //     [evt.target.name]: value,
  //   });
  // };

  const submit = (e) => {
    e.preventDefault();

    setClick(false);
  };
  // const Blur=()=>{
  //   setClick(false)
  // }

  const onClick = (e) => {
    e.preventDefault();
    setClick(true);
  };

  return (
    <div className="right-bar">
      <div className="noted ">
        <BarNote
          onclick={(e) => {
            onClick(e);
          }}
          submitB={(e) => submit(e)}
          click={click}
        />

        <br />
      </div>
      <br />
      <div className="dp-f display-notes ">
        <div className="dp-f border-2px">
          <CardNote /> <CardNote />{" "}
        </div>


  {!loading ? (
    users && users.length > 0?
      (
        <form action="">
    {
      users.map(({id,name})=>(
        <label key={id} htmlFor="">User Company:
        <input type="text" name="name" value={name}/>
        </label>
      ))
    }
        </form>
      ):(<div>No Users</div>)

   ):(
     <div>loading users</div>
   )}

      </div>
    </div>
  );
};
export default Notes;
