import React from 'react';
import PropTypes from 'prop-types';

const LabelInputs = props => {
    const { placeholder, value, visible } = props;
    return (
        <div className="col" style={{ position: 'relative' }}>
            {visible && value ? (
                <span
                    style={{
                        position: 'absolute',
                        fontSize: '11px',
                        fontWeight: '200',
                        top: '-4px',
                        background: '#fff'
                    }}
                >
                    {placeholder}
                </span>
            ) : (
                ''
            )}
            {/* <input {...props} onFocus={enableSpan} onBlur={disableSpan}/> */}
            <input {...props} />
        </div>
    );
};

export default LabelInputs;
