import React ,{useState} from "react";
import { NavLink } from "react-router-dom";
import Icons from "../../assets/Icons";

const Sidebar = () => {
    const length ={width: '270px'}
    const Newlength ={}
    const[open ,setOpen]=useState(false);
    const[close ,setclose]=useState(false);
    const[style ,setStyle]=useState(length);


    const openSidebar=()=>{
setOpen(true);

    }

    const closeSidebar=()=>{
        setclose(true)
    }


  return (
    <div className="sidebar"  style={style}      onMouseOver={openSidebar}
    onMouseOut={closeSidebar}>
      <ul className="ul-list">
        <NavLink activeClassName="selected" to="/" exact={true}>
          {" "}
          <li>
            <Icons focusable="false" name="Bulb" size="24" />
            <h4>Notes</h4>
          </li>
        </NavLink>
        <NavLink activeClassName="selected" to="/changeinPrice">
          {" "}
          <li>
            <Icons focusable="false" name="Notification" size="24" />
            <h4>Reminders</h4>
          </li>
        </NavLink>
        <NavLink activeClassName="selected" to="/dailyReturn">
          {" "}
          <li>
            <Icons focusable="false" name="Label" size="24" />
            <h4> Labels</h4>
          </li>
        </NavLink>
        <NavLink activeClassName="selected" to="/movingAverage">
          <li>
            <Icons focusable="false" name="Pencil" size="24" />
            <h4>Modify Labels</h4>
          </li>
        </NavLink>
        <NavLink activeClassName="selected" to="/risks">
          {" "}
          <li>
            <Icons focusable="false" name="Archive" size="24" />
            <h4>Archive</h4>
          </li>
        </NavLink>{" "}
        <NavLink activeClassName="selected" to="/predictions">
          <li>
            <Icons focusable="false" name="Delete" size="24" />
            <h4>Basket</h4>
          </li>
        </NavLink>
      </ul>
    </div>
  );
};
export default Sidebar;
