import {getAllNotes,createNote} from '../api/keep.api'

class Note {

   constructor() {
       this.respond = {
           success: false,
           response: null,
           message: 'An error occured while connecting to the server!'
       };
   }

 setResponse = data => {
   this.respond = data;
};

getResponse = () => {
   return this.respond;
};

addNote = async ({id,note}) => {
   await createNote(id,note).then(({ result }) => {
       const { success, message, response } = data;
       if (success) {
           this.setResponse(response);
       } else {
           this.setResponse([]);
       }
   });
};

getNotes = async (userId) => {

   await getAllNotes(userId).then(( result ) => {
       const { data } = result;
       const {success,response,message}=data;

       if (success) {
           this.setResponse(response);
       } else {
           console.log(message);
       }
   });
};
}
export default new Note();
